class AppLayout {
   static Layout = { NORMAL: -1, FULL: -2 };

   context;

   requests = [];

   constructor(context) {
      this.context = context;
   }

   setLayout(mode) {
      switch (mode) {
         default:
            return;
         case AppLayout.Layout.NORMAL:
         case AppLayout.Layout.FULL:
      }

      const page = this.context.currentPageKey;
      const view = this.context.currentViewKey;

      const request = this.requests.find((request) => {
         return request.page === page && request.view === view;
      });
      if (request) request.mode = mode;
      else this.requests.push({ page, view, mode });
   }

   getCurrentRequest() {
      const page = this.context.currentPageKey;
      const view = this.context.currentViewKey;

      const request = this.requests.find((request) => {
         return request.page === page && request.view === view;
      });

      return request ? request : null;
   }
   getCurrentLayout() {
      const request = this.getCurrentRequest();
      return request ? request.mode : AppLayout.Layout.FULL;
   }

   isNormal() {
      return this.getCurrentLayout() === AppLayout.Layout.NORMAL;
   }
   isFull() {
      return this.getCurrentLayout() === AppLayout.Layout.FULL;
   }
}
class Navigation {
   static Visibility = { NONE: -1, EXPANDED: -2, COLLAPSED: -3 };
   static Layout = { WIDE: -1, THIN: -2 };

   context;

   requests = [];
   layoutRequests = [];

   constructor(context) {
      this.context = context;
   }

   setVisibility(visibility) {
      switch (visibility) {
         default:
            return;
         case Navigation.Visibility.NONE:
         case Navigation.Visibility.EXPANDED:
         case Navigation.Visibility.COLLAPSED:
      }

      const page = this.context.currentPageKey;
      const view = this.context.currentViewKey;

      const request = this.requests.find((request) => {
         return request.page === page && request.view === view;
      });
      if (request) request.visibility = visibility;
      else this.requests.push({ page, view, visibility });
   }
   setLayout(layout) {
      switch (layout) {
         default:
            return;
         case Navigation.Layout.WIDE:
         case Navigation.Layout.THIN:
      }

      const page = this.context.currentPageKey;
      const view = this.context.currentViewKey;

      const request = this.layoutRequests.find((request) => {
         return request.page === page && request.view === view;
      });
      if (request) request.layout = layout;
      else this.layoutRequests.push({ page, view, layout });
   }

   getCurrentRequest() {
      const page = this.context.currentPageKey;
      const view = this.context.currentViewKey;

      const request = this.requests.find((request) => {
         return request.page === page && request.view === view;
      });

      return request ? request : null;
   }

   getCurrentLayoutRequest() {
      const page = this.context.currentPageKey;
      const view = this.context.currentViewKey;

      const request = this.layoutRequests.find((request) => {
         return request.page === page && request.view === view;
      });

      return request ? request : null;
   }

   getCurrentVisibility() {
      const request = this.getCurrentRequest();
      return request ? request.visibility : Navigation.Visibility.COLLAPSED;
   }
   getCurrentLayout() {
      const request = this.getCurrentLayoutRequest();
      return request ? request.layout : Navigation.Layout.WIDE;
   }

   isNone() {
      return this.getCurrentVisibility() === Navigation.Visibility.NONE;
   }
   isExpanded() {
      return this.getCurrentVisibility() === Navigation.Visibility.EXPANDED;
   }
   isCollapsed() {
      return this.getCurrentVisibility() === Navigation.Visibility.COLLAPSED;
   }

   isWide() {
      return !this.isThin();
   }
   isThin() {
      if (this.isDrawer()) return false;

      const { window } = this.context;
      const { innerWidth } = window;

      if (this.getCurrentLayout() === Navigation.Layout.WIDE) {
         return innerWidth <= 1000;
      }
      return this.getCurrentLayout() === Navigation.Layout.THIN;
   }

   isDrawer() {
      const { window } = this.context;
      const { innerWidth } = window;
      return innerWidth <= 600;
   }
}
class Notification {
   snackbars;

   key;
   isShowing = false;

   icon;
   text;
   isLoading;
   actions;

   constructor(context, param = { text, isLoading, icon, actions }) {
      this.snackbars = context.snackbars;

      this.key = context.keyGetter.get();

      this.icon = param.icon;
      this.isLoading = param.isLoading;
      this.text = param.text;
      this.actions = param.actions;
   }

   get index() {
      return this.snackbars.indexOf(this);
   }

   show(timeout = 3000) {
      setTimeout(() => (this.isShowing = true), 80);
      setTimeout(() => this.hide(), timeout);
      return this;
   }
   hide() {
      this.isShowing = false;

      setTimeout(() => {
         this.snackbars.splice(this.snackbars.indexOf(this), 1);
      }, 80);

      return this;
   }
}
class TimeNowGetter {
   #last = 0;

   get() {
      this.#last = 0;
      let now = Date.now();

      while (this.#last >= now) {
         now++;
      }

      this.#last = now;
      return now;
   }
}

import Vue from "vue";
import Router from "./main.router.js";
import Stores from "./main.stores.js";
import Mixin from "./main.mixin.js";

import App from "./app/App.vue";
import ApiHost from "./host/ApiHost.js";

import PageManage from "./pages/manage/PageManage.vue";
import PageUsers from "./pages/users/PageUsers.vue";

Vue.config.productionTip = false;
Vue.prototype.host = ApiHost;
Vue.mixin(Mixin);

class Util {
   static objectToArray(object = {}) {
      return Object.keys(typeof object === "object" ? object : {}).map((key) => ({
         key,
         value: object[key],
      }));
   }
   static confineRouteQuery(previousQuery, nextQuery) {
      let nextQueries = Util.objectToArray(previousQuery);
      let queries = Util.objectToArray(nextQuery);
      let isChanged = false;

      for (let query of queries) {
         const current = nextQueries.find((current) => current.key === query.key);

         if (current && current.value !== query.value) {
            current.value = query.value;
            isChanged = true;
         } else if (!current && query.value !== null && query.value !== undefined) {
            nextQueries.push({ key: query.key, value: query.value });
            isChanged = true;
         }

         if (current && current.value === null) {
            nextQueries.splice(nextQueries.indexOf(current), 1);
            isChanged = true;
         }
      }

      if (!isChanged) return;

      return nextQueries.reduce((query, newQuery) => {
         query[newQuery.key] = newQuery.value;
         return query;
      }, {});
   }
   static parseIconUrl(name) {
      return ApiHost.res(`icon/${name}.svg`);
   }
   static putResource(context, component) {
      let { key, resource } = component;
      key = typeof key === "string" ? key : "";
      resource = typeof resource === "object" ? resource : {};

      let { title, icon } = resource;
      title = typeof title === "string" ? title.trim() : "";
      icon = typeof icon === "object" ? icon : null;

      context.key = key;
      context.title = title;
      context.icon =
         icon === null
            ? null
            : {
                 light: Util.parseIconUrl(icon.light),
                 dark: Util.parseIconUrl(icon.dark),
              };

      return context;
   }
}

new Vue({
   host: ApiHost,
   router: Router,
   store: Stores.store,

   data() {
      return {
         console: {
            log(param1, param2) {
               param2 === undefined ? console.log(param1) : console.log(param1, param2);
            },
            error(param1, param2) {
               param2 === undefined
                  ? console.error(param1)
                  : console.error(param1, param2);
            },
         },
         keyGetter: new TimeNowGetter(),
         window: { innerWidth: 0, innerHeight: 0 },

         appLayout: null,
         navigation: null,
         snackbars: [],
      };
   },
   computed: {
      user: (c) => c.loginStore.getters.user,

      // app
      app: (c) => (c.$children.length ? c.$children[0] : null),

      // pages
      pages() {
         if (!Array.isArray(App.children)) return [];

         return App.children.map((Page) => {
            const Views = Array.isArray(Page.children) ? Page.children : [];
            const Sections = Views.reduce((Sections, View) => {
               if (View === PageUsers && (!this.user || !this.user.isTypeAdmin())) {
                  return Sections;
               }

               const group = typeof View.group === "object" ? View.group : {};
               const key =
                  typeof group.key === "string" ? group.key.trim().replace(" ", "") : "";
               const title = typeof group.title === "string" ? group.title : "";

               let Section = Sections.find((Section) => Section.key === key);
               if (!Section) Sections.push((Section = { key, title, Views: [] }));
               Section.Views.push(View);

               return Sections;
            }, []);

            if (Page === PageManage && !this.user) return Util.putResource({}, Page);

            const sections = Sections.map((Section) => {
               let { title, Views } = Section;
               Views = Array.isArray(Views) ? Views : [];

               let views = Views.map((View) => Util.putResource({}, View));
               let section = Util.putResource({ title, views }, Section);
               section.title = title;

               return section;
            });

            return Util.putResource({ sections }, Page);
         });
      },
      paths: (c) => c.$route.path.split("/").filter((path) => path),
      currentPaths() {
         let { fullPath } = this.$route;

         let questionMarkIndex = fullPath.indexOf("?");
         if (questionMarkIndex !== -1) {
            fullPath = fullPath.substring(0, questionMarkIndex);
         }

         return fullPath.split(/[/]/).filter((path) => path);
      },
      currentPageKey() {
         let paths = this.currentPaths;
         return paths.length > 0 ? paths[0] : "";
      },
      currentViewKey() {
         let paths = this.currentPaths;
         return paths.length > 1 ? paths[1] : "";
      },

      // app layout mode
      APP_LAYOUT_MODE: (c) => ({
         NORMAL: AppLayout.Layout.NORMAL,
         FULL: AppLayout.Layout.FULL,
      }),
      appLayoutCurrentRequest: (c) => c.appLayout.getCurrentLayout(),
      appLayoutMode: (c) => c.appLayoutCurrentRequest,

      // navigation
      DRAWER_VISIBILITY: (c) => {
         return {
            NONE: Navigation.Visibility.NONE,
            EXPANDED: Navigation.Visibility.EXPANDED,
            COLLAPSED: Navigation.Visibility.COLLAPSED,
         };
      },
      DRAWER_LAYOUT: (c) => {
         return {
            WIDE: Navigation.Layout.WIDE,
            THIN: Navigation.Layout.THIN,
         };
      },
   },
   watch: {
      currentPaths() {
         this.closeNavigationDrawer();
      },
   },
   methods: {
      // app layout
      setAppLayout(layout) {
         this.appLayout.setLayout(layout);
      },

      // navigation
      openNavigationDrawer() {
         this.navigation.setVisibility(this.DRAWER_VISIBILITY.EXPANDED);
      },
      closeNavigationDrawer() {
         this.navigation.setVisibility(this.DRAWER_VISIBILITY.COLLAPSED);
      },
      setNavigationDrawerVisibiliy(visibility) {
         this.navigation.setVisibility(visibility);
      },

      // get full icon url on server localhost/res/icon/${name}.svg
      icon(name) {
         console.trace();
         console.warn("please consider using host.icon() instead");
         return this.host.res(`icon/${name}.svg`);
      },

      // send a snackbar notification
      feedback(param) {
         if (typeof param === "string") {
            param = { text: param };
         }

         this.snackbars.push(new Notification(this, param).show());

         return;
         if (this.app) this.app.showSnackbar(param);
      },

      // external interaction
      copyText(text) {
         const textarea = document.createElement("textarea");
         textarea.value = text;
         textarea.setAttribute("readonly", "");
         textarea.style = { position: "absolute", left: "-9999px" };
         document.body.appendChild(textarea);
         textarea.select();
         document.execCommand("copy");
         textarea.remove();
      },
      openLink(link, target = "_blank") {
         let a = document.createElement("a");
         a.style = {
            position: "absolute",
            opacity: "0",
            "pointer-events": "none",
         };
         a.href = link;
         a.target = target;
         a.dispatchEvent(
            new MouseEvent("click", {
               view: window,
               bubbles: true,
               cancelable: false,
            })
         );
         a.remove();
      },
      pushDownload(filename, content) {
         const element = document.createElement("a");
         element.href = `data:text/plain;charset=utf-8,${encodeURIComponent(content)}`;
         element.download = filename;
         document.body.appendChild(element);
         element.click();
         document.body.removeChild(element);
      },

      // routes
      nextRoute(param = {}) {
         this.setRoute(param, true);
      },
      replaceRoute(param = {}) {
         this.setRoute(param, false);
      },
      setRoute(param = {}, isNext = true) {
         let query = Util.confineRouteQuery(this.$route.query, param.query);
         if (isNext) this.$router.push({ query });
         else this.$router.replace({ query });
      },

      // window
      invalidateWindow() {
         this.window.innerWidth = window.innerWidth;
         this.window.innerHeight = window.innerHeight;
      },
   },
   created() {
      this.appLayout = new AppLayout(this);
      this.navigation = new Navigation(this);
      window.addEventListener("resize", this.invalidateWindow);

      // todo implement inheritable onBack functions
      // window.addEventListener("keyup", (event) => {
      //    console.log(event);
      // });
   },
   mounted() {
      this.invalidateWindow();
   },

   render: (createElement) => createElement(App),
}).$mount("#app");
