import ModuleProduct from "@/../../express/data_structures/Product.js";
import ModuleStock from "@/../../express/data_structures/ProductStock.js";
import ModuleBundle from "@/../../express/data_structures/ProductBundle.js";

import Image from "./Image.js";

import Brand from "./Brand.js";
import Category from "./Category.js";
import ProductSpecType from "./ProductSpecType.js";
import ProductSpecContent from "./ProductSpecContent.js";
import ProductPrice from "./ProductPrice.js";
import ItemSearcher from "./ItemSearcher.js";

const textContains = ItemSearcher.textContains;

class ProductUi {
   static #spec = {
      processor: {
         intel: ["i9", "i7", "i5", "i3", "celeron", "pentium"],
         amd: ["ryzen 7", "ryzen 5", "ryzen 3", "althon silver"],
      },
      ram: {
         ddr3: ["16gb", "8gb", "7gb", "6gb", "5gb", "4gb", "3gb", "2gb"],
         ddr4: ["16gb", "8gb", "7gb", "6gb", "5gb", "4gb", "3gb", "2gb"],
      },
      size: ["15.6", "14", "13", "13.1", "11.5"],
      resolution: ["full hd", "fhd", "hd"],
      display: ["full hd", "fhd", "hd"],
      storage: {
         ssd: ["1tb", "1000gb", "512gb", "480gb", "256gb", "240gb", "128gb", "120gb"],
         hdd: [
            "4tb",
            "4000gb",
            "2tb",
            "2000gb",
            "1tb",
            "1000gb",
            "640gb",
            "500gb",
            "320gb",
            "250gb",
         ],
      },
      graphic: [
         "intel hd",
         "intel uhd",
         "intel iris xe",
         "intel iris plus",
         "nvidia geforce gtx",
         "nvidia geforce rtx",
         "nvidia gtx",
         "nvidia rtx",
         "amd radeon",
      ],
   };
   static #color = {
      processor: "#276EB0",
      ram: "#249696",
      size: "#3B9511",
      resolution: "#A11357",
      display: "#A11357",
      storage: "#276EB0",
   };

   constructor(item) {
      this.item = item;
   }

   stockLabels() {
      let labels = [];

      if (this.item.isPricePromotion())
         labels.push({ name: "promotion", text: "Promotion", color: "#FF8A00" });
      if (!this.item.isStockAvailable())
         labels.push({ name: "outOfStock", text: "Out of Stock", color: "#FF4B33" });
      if (this.item.isStockSecondHand())
         labels.push({ name: "secondHand", text: "Second Hand", color: "#249696" });

      return labels;
   }

   specLabels() {
      const { specifications } = this.item;

      return specifications
         .filter((itemSpec) => Object.keys(ProductUi.#spec).includes(itemSpec?.type?.key))
         .map((itemSpec) => {
            const compares = ProductUi.#spec[itemSpec.type.key];
            const content = itemSpec.content.toLowerCase();

            if (
               itemSpec.type.key === ProductSpecType.Key.Processor ||
               itemSpec.type.key === ProductSpecType.Key.Ram ||
               itemSpec.type.key === ProductSpecType.Key.Storage
            ) {
               for (const compareType of Object.keys(compares)) {
                  if (!content.includes(compareType)) {
                     continue;
                  }
                  for (const compare of compares[compareType]) {
                     if (content.includes(compare)) {
                        return {
                           name: `${compareType}${compare}`,
                           text: `${compareType} ${compare}`.toUpperCase(),
                           color: ProductUi.#color[itemSpec.type.key],
                        };
                     }
                  }
               }
            }

            if (itemSpec.type.key === ProductSpecType.Key.Size) {
               for (const compare of compares) {
                  if (content.includes(compare)) {
                     return {
                        name: compare,
                        text: `${compare}"`,
                        color: ProductUi.#color[itemSpec.type.key],
                     };
                  }
               }
            } else if (
               itemSpec.type.key === ProductSpecType.Key.Resolution ||
               itemSpec.type.key === ProductSpecType.Key.Display
            ) {
               for (const compare of compares) {
                  if (content.includes(compare)) {
                     return {
                        name: compare,
                        text: `${compare}`.toUpperCase(),
                        color: ProductUi.#color[itemSpec.type.key],
                     };
                  }
               }
            }

            if (itemSpec.type.key === ProductSpecType.Key.Graphic) {
               for (const compare of compares.map((compare) => compare).reverse()) {
                  if (content.includes(compare)) {
                     return {
                        name: compare,
                        text: `${compare}`.toUpperCase(),
                        color: ProductUi.#color[itemSpec.type.key],
                     };
                  }
               }
            }
         })
         .filter((itemSpec) => itemSpec);
   }
}

class Product {
   static ProductStore = null;
   static BrandStore = null;
   static SpecificationStore = null;

   stock = ModuleStock.trim({});

   static copyInstance(from) {
      const copy = new Product();
      copy.data._id = from.data._id;
      copy.title = from.title;
      copy.description = from.description;
      copy.gifts = from.gifts;
      copy.bundles = from.bundles;
      copy.stock = from.stock ? ModuleStock.trim(from.stock) : ModuleStock.trim({});
      copy.image = from.image;
      copy.brand = from.brand;
      copy.category = from.category;
      copy.specifications = from.specifications;
      copy.price = from.price;
      return copy;
   }
   static importData(data) {
      data = ModuleProduct.trim(data);

      if (typeof data.image === "object" && data.image) data.images.push(data.image);

      const parse = new Product(data._id);
      parse.setTitle(data.title);
      parse.setDescription(data.description);
      parse.setGifts(data.gifts);
      parse.setBundles(data.bundles);
      parse.setBrandId(data.brandId);
      parse.setCategoryId(data.categoryId);
      parse.setSpecifications(data.specifications);
      parse.setImages(data.images);
      parse.setPrice(data.price);

      parse.stock = data.stock;

      return parse;
   }
   static exportFrom(item) {
      let specification = {};
      for (let itemSpecification of item.specifications) {
         specification[itemSpecification.type.key] = itemSpecification.content;
      }

      let price = item.price ? item.price : {};
      let pricePromotion = price.promotion;
      let priceNormal = price.normal;

      return {
         _id: item._id,
         title: item.title ? item.title : undefined,
         description: item.description ? item.description : undefined,
         brandId: item.brand ? item.brand._id : "",
         categoryId: item.category ? item.category._id : "",
         stock: ModuleStock.trim(item.stock),
         gifts: item.gifts.map((gift) => gift),
         bundles: item.bundles.map((bundle) => ModuleBundle.trim(bundle)),
         image: Image.exportFrom(item.image),
         specification,
         price: { normal: pricePromotion, promotion: priceNormal },
      };
   }

   constructor(_id) {
      this.data = { _id };
   }

   compare(item) {
      let value = 0;
      if (!value) value = this.#compareAvailable(item);
      if (!value) value = this.#comparePromotions(item);
      if (!value) value = this.#compareSecondHand(item);
      if (!value) value = this.#compareImage(item);
      if (!value) value = this.#comparePrices(item);
      return value;
   }
   #compareAvailable(item) {
      const stock1 = this.stock ?? { isAvailable: true, isSecondHand: false };
      const stock2 = item.stock ?? { isAvailable: true, isSecondHand: false };
      const isAvailable1 = stock1.isAvailable ?? true;
      const isAvailable2 = stock2.isAvailable ?? true;

      if (isAvailable1 && !isAvailable2) return -1;
      if (!isAvailable1 && isAvailable2) return 1;
      return 0;
   }
   #compareImage(item) {
      const image1 = this.image;
      const image2 = item.image;
      if (image1 && !image2) return -1;
      if (!image1 && image2) return 1;
      return 0;
   }
   #comparePromotions(item) {
      let price1 = this.price;
      let price2 = item.price;

      let promotion1 =
         price1 && typeof price1.promotion === "string" ? price1.promotion.trim() : "";
      let promotion2 =
         price2 && typeof price2.promotion === "string" ? price2.promotion.trim() : "";

      let hasPromotion1 = promotion1.length > 0;
      let hasPromotion2 = promotion2.length > 0;

      if (hasPromotion1 && !hasPromotion2) return -1;
      if (!hasPromotion1 && hasPromotion2) return 1;
      return 0;
   }
   #comparePrices(item) {
      let price1 = this.price;
      let price2 = item.price;

      let normal1 =
         price1 && typeof price1.normal === "string" ? price1.normal.trim() : "";
      let normal2 =
         price2 && typeof price2.normal === "string" ? price2.normal.trim() : "";

      if (!normal1 && normal2) return 1;
      if (normal1 && !normal2) return -1;

      if (normal1 > normal2) return -1;
      if (normal1 < normal2) return 1;
      return 0;
   }
   #compareSecondHand(item) {
      const stock1 = this.stock ?? { isAvailable: true, isSecondHand: false };
      const stock2 = item.stock ?? { isAvailable: true, isSecondHand: false };

      const isSecondHand1 = stock1.isSecondHand ?? false;
      const isSecondHand2 = stock2.isSecondHand ?? false;
      if (isSecondHand1 && !isSecondHand2) return 1;
      if (!isSecondHand1 && isSecondHand2) return -1;
      return 0;
   }

   search(text) {
      const result = {
         keyword: text,
         splitedKeywords:
            typeof text == "string"
               ? text
                    .toLowerCase()
                    .split(/[\s,]+/)
                    .filter((text) => text)
               : [],
         item: this,

         priceMatchedCount() {
            return 0;
         },
         matchedCount() {
            return this.item.search_v2(this.splitedKeywords);
         },
      };

      return result;
   }
   search_v2(texts) {
      let { brand, category, specifications } = this;
      let count = 0;

      for (const text of texts) {
         count += textContains("product", text) ? 1 : 0;
         count += textContains(this.title, text) ? 1 : 0;
         count += textContains(this.description, text) ? 1 : 0;
         count += brand && textContains(brand.title, text) ? 1 : 0;
         count += category && textContains(category.title, text) ? 1 : 0;
         for (const specContent of specifications) {
            let { type, content } = specContent;
            count += type && textContains(type.title, text) ? 1 : 0;
            count += textContains(content, text) ? 1 : 0;
         }
      }

      return count;
   }

   ui() {
      return new ProductUi(this);
   }

   isPricePromotion() {
      let price = this.getPrice();
      if (!price) return false;

      let normalValue = price.normal ? price.normal.getValue() : 0;
      let promotionValue = price.normal ? price.promotion.getValue() : 0;

      return normalValue > 0 && promotionValue > 0 && normalValue > promotionValue;
   }

   getStock() {
      return this.stock;
   }
   isStockAvailable() {
      let stock = this.stock;
      return stock ? stock.isAvailable : true;
   }
   isStockSecondHand() {
      let stock = this.stock;
      return stock ? stock.isSecondHand : false;
   }

   getId() {
      return this.data._id;
   }
   getTitle() {
      return this.data.title;
   }
   getBrandId() {
      return this.data.brandId;
   }
   getCategoryId() {
      return this.data.categoryId;
   }
   getDescription() {
      return this.data.description;
   }
   getPrice() {
      return this.data.price;
   }
   getImages() {
      return this.data.images.map((image) => image);
   }
   getImageThumbnail() {
      return this.images.length ? this.images[0] : null;
   }
   getSpecifications() {
      return [...this.data.specifications];
   }
   getGifts() {
      return Array.isArray(this.data.gifts) ? this.data.gifts.map((gift) => gift) : [];
   }
   getBundles() {
      return (Array.isArray(this.data.bundles) ? this.data.bundles : []).map(
         (bundle) => bundle
      );
   }

   setTitle(data = "") {
      this.data.title = typeof data == "string" ? data.trim() : "";
   }
   setBrandId(brandId) {
      this.data.brandId = brandId;
      Product.BrandStore.dispatch("getItems").then((brands) => {
         this.data.brand = brands.find((brand) => brand._id === brandId);
      });
   }
   setCategoryId(categoryId) {
      this.data.categoryId = categoryId;
      Product.ProductStore.dispatch("getProductCategoryByCategoryID", {
         id: categoryId,
      }).then((category) => {
         this.data.category = category;
      });
   }
   setDescription(data = "") {
      this.data.description = typeof data == "string" ? data.trim() : "";
   }
   setPrice(price) {
      let { normal, promotion } = price ? price : {};
      this.data.price = {
         normal: ProductPrice.parseString(normal ? normal.toString() : ""),
         promotion: ProductPrice.parseString(promotion ? promotion.toString() : ""),
      };
   }

   setImages(images) {
      this.data.images = images
         .filter(
            (image) => typeof image.method === "string" && typeof image.path === "string"
         )
         .map((image) => Image.importData({ method: image.method, path: image.path }));
   }
   addImages(images) {
      this.data.images.push(...images.map((data) => Image.importData(data)));
   }
   removeImage(image) {
      this.data.images = this.data.images.filter(
         (thisImage) => thisImage.path !== image.path
      );
   }

   setSpecifications(specifications = []) {
      if (ProductSpecContent.SpecificationStore === null)
         ProductSpecContent.SpecificationStore = Product.SpecificationStore;

      this.data.specifications = Array.isArray(this.data.specifications)
         ? this.data.specifications
         : [];
      specifications = Array.isArray(specifications) ? specifications : [];

      Promise.all(
         specifications.map((specification) => {
            return ProductSpecContent.importData({
               key: specification.type,
               content: specification.content,
            });
         })
      ).then((specifications) => {
         this.data.specifications = Array.isArray(specifications) ? specifications : [];
      });
   }
   addSpecification(specification) {
      ProductSpecContent.importData({
         key: specification.type,
         content: specification.content,
      }).then((spec) => {
         this.data.specification.push(spec);
      });
   }
   removeSpecification(specification) {
      this.data.specifications = this.data.specifications.filter((thisSpecification) => {
         return !(
            thisSpecification.type === specification.type &&
            thisSpecification.content === specification.content
         );
      });
   }

   setBundles(data = []) {
      this.data.bundles = (Array.isArray(data) ? data : []).map((bundle) =>
         ModuleBundle.trim(bundle)
      );
   }
   addBundle(bundle) {
      this.data.bundles.push(bundle);
   }
   removeBundle(bundle) {
      this.data.bundles = this.data.bundles.filter(
         (thisBundle) => thisBundle.title !== bundle.title
      );
   }

   setGifts(data = []) {
      this.data.gifts = (Array.isArray(data) ? data : [])
         .map((gift) => (typeof gift == "string" ? gift.trim() : ""))
         .filter((gift) => gift);
   }
   addGift(gift) {
      this.data.gifts.push(gift);
   }
   removeGift(gift) {
      this.data.gifts = this.data.gifts.filter((thisGift) => thisGift !== gift);
   }

   // legacies
   get _id() {
      return this.getId();
   }
   get title() {
      return this.getTitle();
   }
   set title(data = "") {
      this.setTitle(data);
   }
   get brand() {
      return this.data.brand;
   }
   set brand(data = {}) {
      this.data.brand = data instanceof Brand ? data : undefined;
      this.data.brandId = data ? data._id : "";
   }
   get category() {
      return this.data.category;
   }
   set category(data) {
      this.data.category = data instanceof Category ? data : undefined;
      this.data.categoryId = data ? data._id : "";
   }
   get description() {
      return this.getDescription();
   }
   set description(data = "") {
      this.setDescription(data);
   }
   get images() {
      return this.getImages();
   }
   get specifications() {
      return this.getSpecifications();
   }
   set specifications(data = []) {
      this.setSpecifications(data);
   }
   get gifts() {
      return this.getGifts();
   }
   set gifts(data = []) {
      this.setGifts(data);
   }
   get bundles() {
      return this.getBundles();
   }
   set bundles(data = []) {
      this.setBundles(data);
   }
}

export default Product;
